package com.api.controller;import com.api.domain.output.WindowSatisfactionOutput;import com.api.service.WindowSatisfactionService;import com.common.response.ResponseResult;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.servlet.http.HttpServletRequest;/** * @author: Administrator */@RestController@RequestMapping(value = "/Satisfaction")@Api(value = "窗口满意率实现率controller",description = "窗口满意率实现率操作",tags = {"窗口满意率实现率操作"})public class WindowSatisfactionController {  @Autowired  private WindowSatisfactionService windowSatisfactionService;  /**   * 获取窗口的满意率和实现率   */  @GetMapping(value = "/findSatisfaction")  @ApiOperation("获取当月窗口的满意率和实现率")  public ResponseResult findDeptSatisfaction() {    WindowSatisfactionOutput windowSatisfactionOutput = windowSatisfactionService.getWindow();    return ResponseResult.success(windowSatisfactionOutput);  }  /**   * 当日满意情况=工号10001被评价满意/不满意跑一次/跑多次   */  @GetMapping(value = "/getEvaluation")  @ApiOperation("当日窗口的满意情况")  public ResponseResult getEvaluation(){    return windowSatisfactionService.getEvaluation();  }}