mapper.update:
update HCHEN_STATISTICAL
set DEPARTMENT = #{department,jdbcType=VARCHAR},
  MONTH_COUNT = #{monthCount,jdbcType=DECIMAL},
  STATISTICAL_DATE_TIME = #{statisticalDateTime,jdbcType=TIMESTAMP}
where ID = #{id,jdbcType=DECIMAL}

mapper.select:
    SELECT
	nvl(SUM(MONTH_COUNT),0)
FROM
	HCHEN_STATISTICAL
where to_char(STATISTICAL_DATE_TIME,'yyyy') = #{year,jdbcType=DECIMAL}
    and to_char(STATISTICAL_DATE_TIME,'MM') = #{month,jdbcType=DECIMAL}
