mapper.select:
select a.EMPLOYEE_ID, a.EMPLOYEE_NAME,a.EMPLOYEE_NO,a.SIGN_IN_TIME,a.SIGN_OUT_TIME,a.IS_LEAVE,a.PUNCH,a.STATUS_NAME,a.BE_LATE,a.LEAVE_EARLY from ATTENDANCE_STATISTICS a LEFT JOIN EMPLOYEES e ON e.id = a.EMPLOYEE_ID where 1=1 AND (EXISTS ( SELECT ROLE_ORGAN.ORGAN_ID FROM USER_ROLE INNER JOIN ROLE_ORGAN ON USER_ROLE.ROLE_ID = ROLE_ORGAN.ROLE_ID WHERE a.ORGANIZATION_ID = ROLE_ORGAN.ORGAN_ID AND USER_ROLE.USER_ID = #{userId}
UNION ALL SELECT organ.ID FROM ORGANIZATION organ WHERE (organ.LINKED_ID =#{orgId} or organ.id = #{orgId}) AND a.ORGANIZATION_ID= organ.id AND organ.AMPUTATED = 0
)
or a.EMPLOYEE_ID = #{employeeId}
)
TO_CHAR (a.ATTENDANCE_DATE,'YYYY-MM-DD') = #{days} and
TO_CHAR (a.ATTENDANCE_DATE,'YYYY-MM-DD') <= #{endDate} and TO_CHAR (a.ATTENDANCE_DATE,'YYYY-MM-DD') >=#{startDate} and
a.STATUS_NAME=#{stateName}
a.EMPLOYEE_NAME like '%'||#{name}||'%' and
a.EMPLOYEE_NO like '%'||#{no}||'%'and
a.ORGANIZATION_ID in ${path}
ORDER by a.ATTENDANCE_DATE desc

select a.* from ATTENDANCE_STATISTICS a LEFT JOIN EMPLOYEES e ON e."ID" = a.EMPLOYEE_ID LEFT JOIN ORGANIZATION o ON e.ORGANIZATION_ID = o.ID where TO_DATE(#{startTime,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') < a.ATTENDANCE_DATE and TO_DATE(#{endTime,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') > a.ATTENDANCE_DATE
AND (EXISTS ( SELECT o.ID FROM USER_ROLE INNER JOIN ROLE_ORGAN ON USER_ROLE.ROLE_ID = ROLE_ORGAN.ROLE_ID WHERE o.id = ROLE_ORGAN.ORGAN_ID AND USER_ROLE.USER_ID = #{userId}
UNION ALL SELECT organ.ID FROM ORGANIZATION organ WHERE (organ.LINKED_ID =#{orgId} or organ.id = #{orgId}) AND o.id = organ.id AND organ.AMPUTATED = 0
)
or a.EMPLOYEE_ID = #{employeeId}
)
a.EMPLOYEE_NAME like '%'||#{name}||'%' and a.EMPLOYEE_NO like '%'||#{no}||'%'and CONCAT(o.PATH ,',') like '%'||#{organizationId}||',%'and

SELECT s.EMPLOYEE_NO,s.ORGANIZATION_NAME,s.EMPLOYEE_NAME,s.EMPLOYEE_ID, sum( CASE s.BE_LATE WHEN '是' THEN 1 ELSE 0 END ) beLateTimes, sum( CASE s.LEAVE_EARLY WHEN '是' THEN 1 ELSE 0 END ) leaveEarlyTimes, sum( CASE s.PUNCH WHEN '是' THEN 1 ELSE 0 END) punchTimes, sum( s.ABSENCE_HOURS ) hours FROM ATTENDANCE_STATISTICS s LEFT JOIN ORGANIZATION o ON s.ORGANIZATION_ID = o.ID where o.AMPUTATED = 0 and to_date(#{startTime,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') < s.ATTENDANCE_DATE and to_date(#{endTime,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') > s.ATTENDANCE_DATE
AND (EXISTS ( SELECT o.ID FROM USER_ROLE INNER JOIN ROLE_ORGAN ON USER_ROLE.ROLE_ID = ROLE_ORGAN.ROLE_ID WHERE o.id = ROLE_ORGAN.ORGAN_ID AND USER_ROLE.USER_ID = #{userId}
UNION SELECT organ.ID FROM ORGANIZATION organ WHERE (organ.LINKED_ID =#{orgId} or organ.id = #{orgId}) AND o.id = organ.id AND organ.AMPUTATED = 0
)
or s.EMPLOYEE_ID = #{employeeId}
)
s.EMPLOYEE_NAME like '%'||#{name}||'%' and s.EMPLOYEE_NO like '%'||#{no}||'%'and CONCAT(o.PATH ,',') like '%'||#{organizationId}||',%'and
GROUP BY EMPLOYEE_NO,EMPLOYEE_NAME,ORGANIZATION_NAME,EMPLOYEE_ID
