package com.personnel.controller;import com.common.response.ResponseResult;import com.personnel.core.base.BaseController;import com.personnel.core.base.BaseService;import com.personnel.model.Dept;import com.personnel.service.DeptService;import io.swagger.annotations.ApiParam;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author: Administrator */@RestController@RequestMapping("/getHikDept")public class DeptController {  @Autowired  private DeptService deptService;  @PostMapping("/formPost")  public ResponseResult formPost() throws Exception {    if (deptService.addDept() != null) {      return ResponseResult.success();    }    return ResponseResult.error("新增失败");  }}