package com.screenData.controller;import com.common.response.ResponseResult;import com.screenData.core.base.BaseController;import com.screenData.core.base.BaseService;import com.screenData.domain.output.MapCenterOutput;import com.screenData.model.MapCenter;import com.screenData.service.MapCenterService;import io.swagger.annotations.Api;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author: XiGuoQing * @description: * @date: Created in 下午 1:56 2018/10/30 0030 * @modified by: */@RestController@RequestMapping(value="/mapCenter")@Api(value = "地图中心controller",description = "地图中心操作",tags = {"地图中心操作接口"})public class MapCenterController extends BaseController<MapCenterOutput, MapCenter,Integer> {  @Autowired  private MapCenterService mapCenterService;  @Override  public BaseService<MapCenterOutput, MapCenter, Integer> getService() {    return mapCenterService;  }  @GetMapping(value="/selectCount")  public ResponseResult selectWindowCount(){    return ResponseResult.success(mapCenterService.selectCount());  }}