package com.screenData.controller;import com.common.response.ResponseResult;import com.screenData.core.base.BaseController;import com.screenData.core.base.BaseService;import com.screenData.domain.output.TotalInterfaceOutput;import com.screenData.model.TotalInterface;import com.screenData.service.TotalInterfaceService;import io.swagger.annotations.Api;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author: XiGuoQing * @description: * @date: Created in 下午 2:06 2018/10/30 0030 * @modified by: */@RestController@RequestMapping(value="/total")@Api(value = "总量controller",description = "总量操作",tags = {"总量操作接口"})public class TotalInterfaceController extends BaseController<TotalInterfaceOutput, TotalInterface,Integer> {  @Autowired  private TotalInterfaceService totalInterfaceService;  @Override  public BaseService<TotalInterfaceOutput, TotalInterface, Integer> getService() {    return totalInterfaceService;  }  @GetMapping(value="/selectCount")  public ResponseResult selectCount(){    return totalInterfaceService.selectCount();  }}